cukes = [list(s) for s in inp]

n = 0
while True:
    n += 1
    next = [["."] * len(cukes[0]) for _ in range(len(cukes))]
    for y in range(len(cukes) - 1, -1, -1):
        for x in range(len(cukes[0]) - 1, -1, -1):
            if cukes[y][x] == ">":
                try:
                    if cukes[y][(x + 1) % len(cukes[0])] == "." and next[y][(x + 1) % len(cukes[0])] == ".":
                        next[y][(x + 1) % len(cukes[0])] = ">"
                    else:
                        next[y][x] = ">"
                except IndexError:
                    pass
    for y in range(len(cukes) - 1, -1, -1):
        for x in range(len(cukes[0]) - 1, -1, -1):
            if cukes[y][x] == "v":
                try:
                    if cukes[(y + 1) % len(cukes)][x] != "v" and next[(y + 1) % len(cukes)][x] == ".":
                    #if next[(y + 1) % len(cukes)][x] == ".":
                        next[(y + 1) % len(cukes)][x] = "v"
                    else:
                        next[y][x] = "v"
                except IndexError:
                    pass
    # print(f"{n}: ")
    if next == cukes:
        print(n)
        print("\n".join("".join(row) for row in next))
        break
    cukes = next
